SQL SQLMSS PREPARE SQLOUT FOR

-*SELECT DISTINCT
SELECT
       STUDENT.STUDENT_SSN AS 'SSN',
       STUDENT.ALTERNATE_ID AS 'ALT_ID',
       STUDENT.LAST_NAME AS 'LNAME',
       STUDENT.FIRST_NAME AS 'FNAME',
       STUDENT.MIDDLE_INIT AS 'MINIT',
       STUDENT.PRIMARY_STREET1 AS 'PRIM_STREET1',
       STUDENT.PRIMARY_STREET2 AS 'PRIM_STREET2',
       STUDENT.PRIMARY_CITY AS 'PRIM_CITY',
       STUDENT.PRIMARY_STATE AS 'PRIM_STATE',
       STUDENT.PRIMARY_ZIPCODE AS 'PRIM_ZIP',
       STUDENT.PRIMARY_PHONE_NUMBER AS 'PRIM_PHONE',

       STU_AWARD_YEAR.AWARD_YEAR_TOKEN AS 'AWYR_TOKEN',
       STU_AWARD_YEAR.GRAD_DT AS 'GRAD_DT',

       STU_AWARD.ACTUAL_AMT AS 'ACTUAL_AMT',

       DISBURSEMENT_TRANSACTIONS.AMOUNT AS 'LOAN_ADV',
       DISBURSEMENT_TRANSACTIONS.XACTION_DATE AS 'XACTION_DT',
       DISBURSEMENT_TRANSACTIONS.AWARD_YEAR_TOKEN AS 'DISB_AWYR_TOK',
       DISBURSEMENT_TRANSACTIONS.POE_TOKEN AS 'POE TOKEN',

       FUNDS.FUND_GOV_ID AS 'GOV_ID',
       FUNDS.FUND_TOKEN AS 'FUND_TOK',
       FUNDS.FUND_SHORT_NAME AS 'FUND_NAME',

       SAY_FM_STU.DATE_OF_BIRTH AS 'BIRTH_DT',
       SAY_FM_STU.ARE_YOU_MALE AS 'ARE_U_MALE',
       SAY_FM_STU.FISAP_INCOME AS 'FAMILY_INC',

       USER_STRING.VALUE_22 AS 'ETHNIC_MIG',
       USER_STRING.VALUE_45 AS 'ACAD_LEVEL'

       FROM STUDENT LEFT OUTER JOIN STU_AWARD_YEAR WITH (NOLOCK) ON (STUDENT.STUDENT_TOKEN = STU_AWARD_YEAR.STUDENT_TOKEN)
                    LEFT OUTER JOIN STU_AWARD WITH (NOLOCK) ON (STU_AWARD_YEAR.STU_AWARD_YEAR_TOKEN = STU_AWARD.STU_AWARD_YEAR_TOKEN)
                    LEFT OUTER JOIN DISBURSEMENT_TRANSACTIONS WITH (NOLOCK) ON (STU_AWARD.STU_AWARD_TOKEN = DISBURSEMENT_TRANSACTIONS.STU_AWARD_TOKEN)
                    LEFT OUTER JOIN FUNDS WITH (NOLOCK) ON (STU_AWARD.FUND_AY_TOKEN = FUNDS.FUND_TOKEN)
                    LEFT OUTER JOIN SAY_FM_STU WITH (NOLOCK) ON (STU_AWARD_YEAR.STU_AWARD_YEAR_TOKEN = SAY_FM_STU.STU_AWARD_YEAR_TOKEN)
                    LEFT OUTER JOIN USER_STRING WITH (NOLOCK) ON (STU_AWARD_YEAR.STU_AWARD_YEAR_TOKEN = USER_STRING.STU_AWARD_YEAR_TOKEN)

WHERE (STU_AWARD_YEAR.AWARD_YEAR_TOKEN = '&AWYEAR.EVAL') AND (STU_AWARD.STATUS = 'A')
-* AND STUDENT.ALTERNATE_ID = 'A12391692'
;
END
-*TABLE FILE SQLOUT PRINT *
-*END
-*-EXIT
DEFINE FILE SQLOUT
DET_TRAN_CODE /I02L= 02;
REC_NUM /I01 = 1;
PROC_GRP /A01 = 'B';
INST_NUM /A05 = '48500';
ACCT_NUM /A11 = SSN || '01';
LOAN_TYPE /A02 = '13';
LSTNAME /A26 = EDIT(LNAME, '99999999999999999999999999');
FSTNAME /A20 = FNAME;
MIDNAME /A02 = IF MINIT NE ' ' THEN (' ' | EDIT (MINIT, '9')) ELSE ' ';
FULLNAME /A50 = LSTNAME || (', ' | FSTNAME || MIDNAME);
STUNAME /A50 = EDIT (FULLNAME, '99999999999999999999999999999999999999999999999999');
STU_NAME /A50 = UPCASE(50, STUNAME, 'A50');
GENDER /A01 = IF ARE_U_MALE EQ 'Y' THEN 'M' ELSE 'F';
ETHNIC_CODE /A01 = DECODE ETHNIC_MIG
                             ('B' '1' 'H' '4' 'HM' '4' 'HP' '4' 'I' '2'
                              'O' '3' 'W' '5' ELSE ' ');
ACADEMIC_LEVEL /A01 = DECODE ACAD_LEVEL
                             ('C1' ' ' 'C2' ' ' 'CE' ' ' 'D1' ' ' 'DC' 'G'
                              'DD' 'G' 'DM' 'G' 'FL' 'G' 'FR' '1' 'GD' 'G'
                              'JR' '3' 'L1' 'A' 'L2' 'B' 'L3' 'C' 'L4' 'D'
                              'LF' 'G' 'LP' 'G' 'LV' 'G' 'NM' ' ' 'PB' '5'
                              'PM' 'A' 'SO' '2' 'SR' '4' 'VC' 'G' 'VD' 'G'
                              'VE' 'G' ELSE ' ');
FAM_INC /A01 = IF (FAMILY_INC LT 6000) THEN '1' ELSE
                          IF (FAMILY_INC GT 5999) AND (FAMILY_INC LT 12000) THEN '2' ELSE
                          IF (FAMILY_INC GT 11999) AND (FAMILY_INC LT 18000) THEN '3' ELSE
                          IF (FAMILY_INC GT 17999) AND (FAMILY_INC LT 24000) THEN '4' ELSE
                          IF (FAMILY_INC GT 23999) AND (FAMILY_INC LT 30000) THEN '5' ELSE '6';

-*LOAN_ADVANCE /I8L = IF LOAN_ADV NE 0 THEN (LOAN_ADV / .01) ELSE 0;

LOAN_ADVANCE /I8L = IF ACTUAL_AMT NE 0 THEN (ACTUAL_AMT / .01) ELSE 0;
PRIM_PHONE /A10 = EDIT(PRIM_PHONE, '9999999999');
PRI_STR1 /A40 = UPCASE(40, PRIM_STREET1, 'A40');
PRI_STR2 /A40 = UPCASE(40, PRIM_STREET2, 'A40');
PRI_CITY /A29 = UPCASE(29, PRIM_CITY, 'A29');
PRI_STATE /A02 = UPCASE(02, PRIM_STATE, 'A02');
PRI_ZIP /A09 = EDIT (PRIM_ZIP, '999999999');
BIRTH_YR /I04L= HPART(BIRTH_DT,'YEAR',BIRTH_YR);
BIRTH_YR_CVT /A04 = FTOA(BIRTH_YR, '(D4Lc)', BIRTH_YR_CVT);
BIRTH_MO /I2L = HPART(BIRTH_DT,'MONTH',BIRTH_MO);
BIRTH_MO_CVT /A02 = FTOA(BIRTH_MO, '(D2Lc)', BIRTH_MO_CVT);
BIRTH_DA /I2L = HPART(BIRTH_DT,'DAY',BIRTH_DA);
BIRTH_DA_CVT /A02 = FTOA(BIRTH_DA, '(D2Lc)', BIRTH_DA_CVT);
BIRTHDT /A08 = BIRTH_MO_CVT || BIRTH_DA_CVT || BIRTH_YR_CVT;

GRAD_YR /I04L= HPART(GRAD_DT,'YEAR',GRAD_YR);
GRAD_YR_CVT /A04 = FTOA(GRAD_YR, '(D4Lc)', GRAD_YR_CVT);
GRAD_MO /I2L = HPART(GRAD_DT,'MONTH',GRAD_MO);
GRAD_MO_CVT /A02 = FTOA(GRAD_MO, '(D2Lc)', GRAD_MO_CVT);
SEP_DT /A08 = GRAD_MO_CVT || GRAD_YR_CVT;
SEPARATE_DT /A04 = EDIT(SEP_DT, '99$$99');

XACTION_YR /I04L= HPART(XACTION_DT,'YEAR',XACTION_YR);
XACTION_YR_CVT /A04 = FTOA(XACTION_YR, '(D4Lc)', XACTION_YR_CVT);
XACTION_MO /I2L = HPART(XACTION_DT,'MONTH',XACTION_MO);
XACTION_MO_CVT /A02 = FTOA(XACTION_MO, '(D2Lc)', XACTION_MO_CVT);
XACTION_DA /I2L = HPART(XACTION_DT,'DAY',XACTION_DA);
XACTION_DA_CVT /A02 = FTOA(XACTION_DA, '(D2Lc)', XACTION_DA_CVT);
XACT_DT /A08 = XACTION_MO_CVT || XACTION_DA_CVT || XACTION_YR_CVT;
ADVANCEDT /A06 = IF (GOV_ID EQ 'N') AND (LOAN_ADVANCE NE 0 OR LOAN_ADVANCE NE MISSING) THEN
                               EDIT(XACT_DT, '9999$$99') ELSE ' ';
AWD_YEAR /I04 = AWYR_TOKEN + 1;
AWARD_YEAR /A04 = FTOA(AWD_YEAR, '(D4c)', AWARD_YEAR);
FUND_TOKEN /A04 = FTOA(FUND_TOK, '(D4c)', FUND_TOKEN);
DISB_AY_TOKEN /A04 = FTOA(DISB_AWYR_TOK, '(D4c)', DISB_AY_TOKEN);
-*PERK_FLG /A01 = IF (FUND_TOKEN EQ '3841') THEN 'Y' ELSE
-* IF (FUND_TOKEN EQ '3961') THEN 'Y' ELSE 'N';
-* Mod Begin 5/22/15 - REM
-*PERK_FLG /A01 = IF (FUND_TOKEN IN ('4904','5024','5590')
-* AND (LOAN_ADV NE 0)) THEN 'Y' ELSE 'N';
PERK_FLG /A01 = IF (FUND_TOKEN IN ('6007','6127','6693')
                          AND (ACTUAL_AMT NE 0)) THEN 'Y' ELSE 'N';
-* Mod End 5/22/15 - REM
DET_FY_COMMIT /I08L= IF ((GOV_ID EQ 'N') AND (PERK_FLG EQ 'Y') AND (ACTUAL_AMT NE 0)) THEN
                             (ACTUAL_AMT / .01) ELSE 0;
MNTS_OF_GRACE_FILL /A02 = ' ';
INT_RATE_FILL /A05 = ' ';
REPAY_PLAN_FILL /A02 = ' ';
FREQENCY_FILL /A01 = ' ';
FIX_PAY_AMT_FILL /A08 = ' ';
IND_STU_FILL /A01 = ' ';
FIRST_TIME_BOR_FILL/A01 = ' ';
DET_AMT_ADV_FILL /A08 = ' ';
FUND_NUM_FILL /A06 = ' ';
REPAY_TERM_FILL /A03 = ' ';
SSN_IND_FILL /A01 = ' ';
FOREIGN_FLG /A01 = ' ';
TITLE_CODE_FILL /A01 = ' ';
REMARKS_FILL /A06 = ' ';
DET_FILLER /A02 = ' ';
END

TABLE FILE SQLOUT
SUM LOAN_ADVANCE DISB_AY_TOKEN PERK_FLG GOV_ID
BY SSN
BY FUND_TOKEN
BY FUND_NAME
WHERE LOAN_ADVANCE GT 0 OR LOAN_ADVANCE LT 0 ;
WHERE PERK_FLG EQ 'Y';
WHERE GOV_ID EQ 'N';
ON TABLE HOLD AS DISB0
-*ON TABLE PCHOLD FORMAT EXL2K
END
-RUN
-*-EXIT
TABLE FILE DISB0
SUM LOAN_ADVANCE DISB_AY_TOKEN PERK_FLG GOV_ID
BY SSN
BY FUND_TOKEN
BY FUND_NAME
WHERE LOAN_ADVANCE NE 0;
ON TABLE HOLD AS DISB1
END
-RUN
-IF &LINES EQ 0 THEN GOTO NORECS;

TABLE FILE SQLOUT
PRINT SSN ALT_ID DET_TRAN_CODE REC_NUM PROC_GRP INST_NUM ACCT_NUM LOAN_TYPE
      STU_NAME PRI_STR1 PRI_STR2 PRI_CITY PRI_STATE PRI_ZIP
      PRIM_PHONE AWARD_YEAR SEPARATE_DT FOREIGN_FLG
      GENDER FAM_INC ETHNIC_CODE ACADEMIC_LEVEL
      ADVANCEDT XACT_DT
   DET_FY_COMMIT GOV_ID
      MNTS_OF_GRACE_FILL INT_RATE_FILL REPAY_PLAN_FILL FREQENCY_FILL FIX_PAY_AMT_FILL
      IND_STU_FILL FIRST_TIME_BOR_FILL DET_FILLER
      DET_AMT_ADV_FILL FUND_NUM_FILL REPAY_TERM_FILL SSN_IND_FILL TITLE_CODE_FILL
      REMARKS_FILL BIRTHDT
      STUNAME
BY SSN NOPRINT
BY LOWEST ADVANCEDT NOPRINT
WHERE (GOV_ID EQ 'N')
  AND LOAN_ADVANCE NE 0;
ON TABLE HOLD AS DETAIL1
END
-RUN

JOIN CLEAR *
JOIN SSN IN DISB1 TO SSN IN DETAIL1 AS J1
-RUN
TABLE FILE DISB1
PRINT SSN ALT_ID DET_TRAN_CODE REC_NUM PROC_GRP INST_NUM ACCT_NUM LOAN_TYPE STU_NAME
      LOAN_ADVANCE ADVANCEDT XACT_DT
      MNTS_OF_GRACE_FILL INT_RATE_FILL
      PRI_STR1 PRI_STR2 PRI_CITY PRI_STATE PRI_ZIP
      PRIM_PHONE AWARD_YEAR SEPARATE_DT FOREIGN_FLG
      GENDER FAM_INC ETHNIC_CODE ACADEMIC_LEVEL
      DET_FY_COMMIT GOV_ID
      REPAY_PLAN_FILL FREQENCY_FILL FIX_PAY_AMT_FILL
      IND_STU_FILL FIRST_TIME_BOR_FILL DET_FILLER
      DET_AMT_ADV_FILL FUND_NUM_FILL REPAY_TERM_FILL SSN_IND_FILL TITLE_CODE_FILL
      REMARKS_FILL BIRTHDT STUNAME
BY SSN NOPRINT
ON TABLE HOLD AS DETAIL2
