TABLE FILE DETAIL2
PRINT DET_TRAN_CODE REC_NUM PROC_GRP INST_NUM ACCT_NUM LOAN_TYPE STU_NAME
      LOANADV_AMT ADVANCEDT
      MNTS_OF_GRACE_FILL INT_RATE_FILL REPAY_PLAN_FILL FREQENCY_FILL FIX_PAY_AMT_FILL
      SEPARATE_DT GENDER ETHNIC_CODE
      IND_STU_FILL FIRST_TIME_BOR_FILL
      ACADEMIC_LEVEL FAM_INC
      DET_AMT_ADV_FILL DET_FY_COMMIT FUND_NUM_FILL REPAY_TERM_FILL SSN_IND_FILL
      PRIM_PHONE REMARKS_FILL
      PRI_STR1 PRI_STR2 PRI_CITY PRI_STATE PRI_ZIP FOREIGN_FLG TITLE_CODE_FILL
